// task_1/dashboard/src/Notifications.js

import React from 'react';

import closeIcon from './close-icon.png'; // Import the close icon image

function Notifications() {
    // Function to handle click on the close button
    const handleClick = () => {
      console.log('Close button has been clicked');
    };

  return (
    <div className="Notifications">
      <p>Here is the list of notifications</p>
        {/* Button with inline styling */}
        <button
        style={{ float: 'right' }} // Aligns the button to the right side of the notifications box
        aria-label="Close" // Accessibility label for the button
        onClick={handleClick} // Event handler for button click
      >
        {/* Image within button */}
        <img src={closeIcon} alt="Close icon" /> {/* Renders the close icon */}
      </button>
      <p>Here is the list of notifications</p> {/* Header for the notifications */}
      {/* Unordered list */}
      <ul>
        <li data-priority="default">New course available</li> {/* Notification with default priority */}
        <li data-priority="urgent">New resume available</li> {/* Notification with urgent priority */}
        {/* Latest notification with HTML formatting */}
        <li dangerouslySetInnerHTML={{ __html: getLatestNotification() }} />
      </ul>
    </div>
  );
}

export default Notifications;
